import React, { useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import Axios from 'axios';

import Footer from "../components/Footer";
import Header from "../components/Header";
import Cyberdropdow from "../components/cyberpunk/Cyber-dropdown-button";

function Home (){

    const [loginStatus, setLoginStatus] = useState("");
    const [show, setShow] = useState(false);
    const history = useHistory();

    Axios.defaults.withCredentials = true;

    useEffect(() => {
        Axios.get("http://localhost:3001/api/login").then((response) => {
          if (response.data.loggedIn === true) {
            setLoginStatus(response.data.user[0].username);
            if(show){
              console.log(show);
            }
          }else {
            history.push("/");
          };
        });
      });

    return(
        <div className="Home">
            <Header/>
                <div className="row">
                <div className="cyber-dropdown-content" style={{ display: show ? "block" : "none" }}>
                            <h1>dropdown</h1>
                          </div>
                    <div className="card-cyberpunk">
                        <div style={{float:"right"}}>
                          <Cyberdropdow name="Profile" tags="j117" action={() => setShow((s) => !s)}/>
                        </div>
                        <h1 >Welcome to Heimdall</h1>
                        <h1>{loginStatus}</h1>
                    </div>
                <div className="card-cyberpunk-wrapper">
                    <h1 >Home</h1>
                </div>
            </div>
            <Footer/>
        </div>
    );
}

export default Home;
