import React, { useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import Axios from 'axios';

import Footer from "../components/Footer";
import Header from "../components/Header";
import Cyberdropdow from "../components/cyberpunk/Cyber-dropdown-button";
import Cyberbutton from "../components/cyberpunk/Cyber-buttons";

function Home (){

    const [loginStatus, setLoginStatus] = useState("");
    const [show, setShow] = useState(true);
    const history = useHistory();

    Axios.defaults.withCredentials = true;

    useEffect(() => {
        Axios.get("http://localhost:3001/api/login").then((response) => {
          if (response.data.loggedIn === true) {
            setLoginStatus(response.data.user[0].username);
            if(show){
              console.log(response);
            }
          }else {
            history.push("/");
          };
        });
      });

      // React redux logout action
    const logout = () => async (dispatch) => {
      try {
        await Axios.get('/api/auth/logout')
        localStorage.removeItem('userInfo')
      } catch (error) {
        console.log(error)
      }
    }

    return(
        <div className="Home">
            <Header/>
                <div className="row">
                    <div className="card-cyberpunk">
                        <div style={{float:"right"}}>
                        <Cyberbutton name="disconnection" tags="J117"/>
                        </div>
                        <h1 >Welcome to Heimdall</h1>
                        <h1>{loginStatus}</h1>
                    </div>
                <div className="card-cyberpunk-wrapper">
                <h1 >Home</h1>
                <h1 >Home</h1>
                <h1 >Home</h1>
                <h1 >Home</h1>
                </div>
            </div>
            <Footer/>
        </div>
    );
}

export default Home;
